const Id = ({ news }) => {
  console.log(news)
  return (
    <h1>Soy el Id</h1>
  )
}

Id.getInitialProps = async ({ query }) => {
  let news;
  try {
    const res = await fetch(`https://api.mercadolibre.com/sites/MLA/search?q=:${query?.search}`);
    news = await res.json();
  } catch (error) {
    console.log(error)
  }
  return {
    news
  }
}

export default Id
