import { withRouter } from 'next/router';
import fetch from 'isomorphic-fetch';

const Id = ({ news }) => {
  console.log(news)
  return (
    <h1>Soy el Id</h1>
  )
}

Id.getInitialProps = async ({ query }) => {
  let news;
  let newsOne;
  console.log('query', query?.id)
    try {
      const res = await fetch(`https://api.mercadolibre.com/items/${query?.id}/description`);
      const resOne = await fetch(`https://api.mercadolibre.com/items/${query?.id}/description`);
      news = await res.json();
      newsOne = await resOne.json();
    } catch (error) {
      console.log('error', error)
    }
  return {
    news
  }
}

export default withRouter(Id)
