import { withRouter } from 'next/router';

const Id = ({ news, resOne }) => {
  console.log('resOne', resOne)
  return (
    <h1>Soy el Id</h1>
  )
}

Id.getInitialProps = async ({ query }) => {
  console.log('query', query)
  let news;
  let resOne
  try {
    const res = await fetch(`https://api.mercadolibre.com/items/${query?.id}`);
    const resOne = await fetch(`https://api.mercadolibre.com/items/${query?.id}/description`);
    news = await res.json();
    newsOne = await resOne.json();
  } catch (error) {
    console.log(error)
  }
  return {
    resOne
  }
}

export default withRouter(Id)
