import { withRouter, useRouter } from 'next/router';
import Router from 'next/router';
import fetch from 'isomorphic-fetch';
import Layout from '../components/Layout';

const Items = ({ news }) => {
  const arrayItems = news?.results;

  const handleSubmit = value => {
    value !== '' && Router.push(`/items?search=${value}`)
  }

  const renderCard = (item, idx) => (
    <div key={idx} className="card">
      <div className="card-body">
        <div className="row">
          <div className="col-sm-4">
            <img src={item.thumbnail} />
          </div>
          <div className="col-sm-8">
            <p>{item.price}</p>
            <p>{item.title}</p>
          </div>
        </div>
      </div>
    </div>
  )

  return(
    <Layout title='Items Page' handleSubmit={handleSubmit}>
      {arrayItems.map((item, idx) => renderCard(item, idx))}
    </Layout>
  )
}

Items.getInitialProps = async () => {
  const router = useRouter()
  console.log(router)
  let news;
  try {
    const res = await fetch(`https://api.mercadolibre.com/sites/MLA/search?q=:${router?.query?.search}`);
    news = await res.json();
  } catch (error) {
    console.log(error)
  }
  return {
    news
  }
}

export default withRouter(Items)
