import Link from 'next/link';
import "../styles/styles.scss";

const Layout = ({ title, children, route}) => {
  return (
    <div className='header'>
      <Head><title>{title}</title></Head>
      <div>
        <Link href="/"><a className='nav-logo'>Mercado Libre</a></Link>
      </div>
      <div className='input'>
        <input className='nav-search-input' onChange={event => console.log(event.target.value)}/>
        <button>Press</button>
        <Link href={`/${route}`}><a>Press</a></Link>
      </div>
      <div/>
      { children }
    </div>
  )
}

export default Layout;
