import { withRouter } from 'next/router';
import Router from 'next/router';
import fetch from 'isomorphic-fetch';
import Layout from '../components/Layout';

const Items = ({ news }) => {
  const arrayItems = news?.results;

  const handleSubmit = value => {
    console.log('value', value)
    value !== '' && Router.push(`/items?search=${value}`)
  }

  const renderCard = (item) => (
    <div className="card">
      <div className="card-body">
        {item.price}
      </div>
    </div>
  )

  return(
    <Layout title='Items Page' handleSubmit={handleSubmit}>
      {arrayItems.map(item => renderCard(item))}
    </Layout>
  )
}

Items.getInitialProps = async ({ router }) => {
  let news;
  try {
    const res = await fetch(`https://api.mercadolibre.com/sites/MLA/search?q=:${router?.query?.search}`);
    news = await res.json();
  } catch (error) {
    console.log(error)
  }
  return {
    news
  }
}

export default withRouter(Items)
