const Id = ({ news, resOne }) => {
  console.log(resOne)
  return (
    <h1>Soy el Id</h1>
  )
}

Id.getInitialProps = async ({ query }) => {
  let news;
  let resOne
  try {
    const res = await fetch(`https://api.mercadolibre.com/items/${query?.search}`);
    const resOne = await fetch(`https://api.mercadolibre.com/items/${query?.search}/description`);
    news = await res.json();
    newsOne = await resOne.json();
  } catch (error) {
    console.log(error)
  }
  return {
    news, resOne
  }
}

export default Id
