import { withRouter } from 'next/router';

const Id = ({ news }) => {
  console.log(news)
  return (
    <h1>Soy el Id</h1>
  )
}

Id.getInitialProps = async ({ query }) => {
  let news;
  try {
    const news = await fetch(`https://api.mercadolibre.com/items/${query?.id}/description`);
    news = await res.json();
  } catch (error) {
    console.log('error', error)
  }
  return {
    news
  }
}

export default withRouter(Id)
