import { useState } from 'react';
import Router from 'next/router';
import "../styles/styles.scss";
import Layout from '../components/Layout';
import fetch from 'isomorphic-fetch';

const Index = ({ news }) => {

  const handleSubmit = value => {
    Router.push(`/items/?searchItem=${news}`)
    console.log('handleSubmit', value)
  }

  return (
    <Layout route='items' handleSubmit={handleSubmit} title='Home Page'></Layout>
  )
};

Index.getInitialProps = async ({ query }) => {
  let news;
  try {
    const res = await fetch(`https://api.mercadolibre.com/sites/MLA/search?q=:${query.searchItem}`);
    news = await res.json();
  } catch (error) {
    console.log(error)
  }
  return {
    news
  }
}

export default Index;
